import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Food } from '../model/food';
import { RestaurantService } from '../service/restaurant.service';

@Component({
  selector: 'app-food-list',
  templateUrl: './food-list.component.html',
  styleUrls: ['./food-list.component.css']
})
export class FoodListComponent implements OnInit {
  restaurantId: string = '';
  foods: Food[] = [];

  constructor(private restaurantService: RestaurantService, private route: ActivatedRoute) {}

  ngOnInit(): void {
    this.route.paramMap.subscribe(params => {
      this.restaurantId = params.get('Id');
      if (this.restaurantId) {
        this.getAllFoodMenu(restaurantId);
      }
    });



    this.restaurantService.getAllFoodMenu().subscribe({
      next: (data: foodMenu[]) => {
        this.restaurants = data;
        this.route.paramMap.subscribe((params) => {
          const restaurantId = params.get('id');
          this.getRestaurant(restaurantId);
        });
      },
      error: (err: any) => {
        alert(err);
      }
    });



  }

  getAllFoodMenu(restaurantId: string): void {
    this.restaurantService.getAllFoodMenu(restaurantId).subscribe(
      (data: Food[]) => {
        this.foods = data;
      },
      (error) => {
        console.error('Error fetching foods:', error);
        if (error.status === 404) {
          alert('Restaurant not found or no food available.');
        } else {
          alert('Error fetching foods. Please try again later.');
        }
      }
    );
  }
}
<section>
  <app-food-card *ngFor="let food of foods" [food]="food" ></app-food-card>
</section>
