import { Component, Input, OnInit } from '@angular/core';
import { Restaurant } from '../model/restaurant';

@Component({
  selector: 'app-restaurent-card',
  templateUrl: './restaurant-card.component.html',
  styleUrls: ['./restaurant-card.component.css']
})


export class RestaurentCardComponent implements OnInit {
[x: string]: any;

  @Input()
  rest?: Restaurant;
  constructor() { }

  ngOnInit(): void {

  }
  }



<!--
  <mat-card class="restaurant" >
  <img mat-card-image id="image" src="../../assets/restaurantimage/{{rest.restaurantName}}.jpg"   >
  <mat-card-header class="allcontent">
      <mat-card-title class="restaurant-title">{{rest.restaurantName}}</mat-card-title>
      <mat-card-subtitle class="content">Description: {{rest.description}} <br> Location: {{rest.location}} <br> Rating: {{rest.rating}}</mat-card-subtitle>
  </mat-card-header>

</mat-card>-->


<div *ngFor="let restaurant of restaurants" (click)="openFoodMenu(restaurant.id)">
  <mat-card class="restaurant">
    <img mat-card-image id="image" src="../../assets/restaurantimage/{{restaurant.restaurantName}}.jpg">
    <mat-card-header class="allcontent">
        <mat-card-title class="restaurant-title">{{restaurant.restaurantName}}</mat-card-title>
        <mat-card-subtitle class="content">Description: {{restaurant.description}} <br> Location: {{restaurant.location}} <br> Rating: {{restaurant.rating}}</mat-card-subtitle>
    </mat-card-header>
  </mat-card>
</div>
