import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private isAuthenticated = false;
  private userEmail: string | null = null;

  constructor(private http:HttpClient) { }
  saveUser(userDetails: any) {
    console.log("inside auth services")
    return this.http.post(`http://localhost:9000/api/v1/save `, userDetails, {
      observe: 'response', withCredentials: true  // withCredentials: true => mean browser can stored data inside the cookies related to thise request
    });
  }


  login(user: any): Observable<any> {
    return this.http.post<any>(`http://localhost:9000/api/v1/login`, user)
      .pipe(
        // Assuming your API returns user data with an email field
        map(response => {
          this.isAuthenticated = true;
          this.userEmail = response.email;
          return response;
        })
      );
  }
  logout() {
    this.isAuthenticated = false;
  }

  isAuthenticatedUser() {
    return this.isAuthenticated;
  }
}
