import { Component, Inject } from '@angular/core';
import { MAT_DIALOG_DATA, MatDialogRef } from '@angular/material/dialog';
import { Router } from '@angular/router';
import { RestaurantAddComponent } from '../restaurant-add/restaurant-add.component';
@Component({
  selector: 'app-admin',
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.css']
})
export class AdminComponent {

  constructor(private route:Router){}

  role2:any = localStorage.getItem("role");

  add(){
    this.route.navigateByUrl('/restaurant-add');
  }

  addFoodItem(){
    this.route.navigateByUrl('/adminFoodItem');
  }  
}
<!-- <app-header></app-header> -->
<div class="background">
    <h1>Admin Dashboard:  </h1>

    <button mat-raised-button   (click)="add()" class="addRes">Add Restaurant</button>


</div>


<!-- <app-header></app-header> -->
<div class="background">
  <h1>Admin Dashboard</h1>
<div class="shape">
  <button mat-raised-button color="primary" (click)="add()" class="addRes">Add or edit restaurant</button>
  <hr>
 <button  mat-raised-button color="accent" (click)="addFoodItem()" id="addItem">Add or edit new Food item</button>
</div>
</div>
