<div class="body-container">

  <img src="../../assets/images/Anime Food Wallpaper aesthetic HD.jpeg" alt="Food Background Image" class="food-image">
</div>
<div class="registration-form">

  <h2>Registration</h2>
  <form [formGroup]="userForm" (ngSubmit)="onSubmit()">

    <div class="form-group">
      <label for="email">Email:</label>
      <input type="email" id="email" formControlName="emailId" />
      <div id="err" *ngIf="userForm.get('emailId')?.hasError('required') && userForm.get('emailId')?.touched">
        Email is required.
      </div>
      <div id="err" *ngIf="userForm.get('emailId')?.hasError('emailId') && userForm.get('emailId')?.touched">
        Please enter a valid email address.
      </div>
    </div>
    <div class="form-group">
      <span>Password</span>
      <input type="password" placeholder="Enter your Password" formControlName="password">
      <div id="err" *ngIf="password?.errors?.['required'] && password?.dirty">
          Password is required
      </div>
      <div id="err" *ngIf="password?.errors?.['pattern']">
          <span color="red"> should have min 7 letter password, with at least a symbol, upper and lower case letters
              and a number.</span>
      </div>
  </div>
