import { Component, Input, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { RestaurantService } from '../service/restaurant.service';
import { Restaurant } from '../model/restaurant';

@Component({
  selector: 'app-restaurant-list',
  templateUrl: './restaurent-list.component.html',
  styleUrls: ['./restaurent-list.component.css'],
})
export class RestaurentListComponent implements OnInit {
  @Input()
  restaurants: Array<Restaurant> = [];
  filteredRestaurants: Array<Restaurant> = [];

  constructor(
    private restaurantService: RestaurantService,
    private route: ActivatedRoute
  ) {}

  ngOnInit(): void {
  //   this.restaurantService.getAllRestaurants().subscribe({
  //     next: (data) => {
  //       this.restaurants = data;
  //       this.route.paramMap.subscribe((params) => {
  //         const restaurantId = params.get('id');
  //         this.getRestaurant(restaurantId);
  //       });
  //     },
  //     error: (err) => {
  //       alert(err);
  //     }
  //   });
  // }
  this.restaurantService.getAllRestaurants().subscribe({
    next:(data:Array<Restaurant>)=>{
      this.restaurants = data;
      console.log(this.restaurants)
    },
    error:err=>{
      alert(err);
    }
  });
}

  private getRestaurant(restaurantId: string | null): void {
    if (restaurantId) {
      this.restaurantService['getRestaurant'](restaurantId).subscribe({
        next: (restaurant: Restaurant) => {
          this.filteredRestaurants = [restaurant];
        },
        error: (err: any) => {
          alert(err);
        }
      });
    } else {
      this.filteredRestaurants = this.restaurants;
    }
  }
}
