<mat-toolbar class="header">
  <span class="logo">TheFoodExpress</span>
  <span class="spacer"></span>

  <button class="registration-btn" mat-button id="home" routerLink="/">Home</button>
  <button class="registration-btn" mat-button routerLink="/registration">Register</button>

  <div *ngIf="authService.isAuthenticatedUser(); else loginLink" class="user-info">
    <div class="user-info-text" [matMenuTriggerFor]="menu">Welcome {{ authService.userEmail?.split('@')[0] || 'User' }}</div>
    <!-- <mat-icon class="dropdown-icon" [matMenuTriggerFor]="menu">expand_more</mat-icon> -->
  </div>

  <ng-template #loginLink>
    <button class="login-btn" mat-button routerLink="/login">Login</button>
  </ng-template>

  <mat-menu #menu="matMenu">
    <button mat-menu-item (click)="logout()">Logout</button>
  </mat-menu>
</mat-toolbar>
