// header.component.ts

import { Component, OnInit } from '@angular/core';
import { AuthService } from '../service/auth.service';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements OnInit {
  loggedInUser: string;

  constructor(private authService: AuthService) { }

  ngOnInit(): void {
    this.loggedInUser = this.authService.getUserEmail();
  }

  onLogout(): void {
    this.authService.logout();
  }
}
 <mat-toolbar class="header"  >
  <span class="logo">TheFoodExpress</span>
  <span class="spacer"></span>

 <button class="registration-btn" mat-button  id="home" routerLink="/" >Home</button>
  <button class="registration-btn" mat-button  routerLink="/registration">Register</button>
  <!-- <button class="login-btn" mat-button routerLink="/login">Login</button> -->
  <button class="login-btn" mat-button *ngIf="authService.isAuthenticatedUser(); else loginLink">
    <a (click)="logout()">Logout</a>
  </button>
  <ng-template #loginLink>
    <button class="login-btn" mat-button routerLink="/login">Login</button>
  </ng-template>


</mat-toolbar>
