import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { FoodMenuService } from '../service/food-menu.service';

@Component({
  selector: 'app-admin-food-item',
  templateUrl: './admin-food-item.component.html',
  styleUrls: ['./admin-food-item.component.css']
})
export class AdminFoodItemComponent {

  addform: FormGroup;

  constructor(private fb: FormBuilder, private foodMenuService: FoodMenuService, private router: Router) {
    this.addform = this.fb.group({
      // Define your form controls here
      itemName: ['', Validators.required],


    });
  }

  addFoodMenuItem() {
    if (this.addform.valid) {
      const foodMenuItemData = this.addform.value;
      this.foodMenuService.registerFoodMenuItem(foodMenuItemData).subscribe(
        response => {
          alert('Food menu item registered successfully');
          // Reset the form after successful registration
          this.addform.reset();
          // Redirect to the desired page
          this.router.navigateByUrl("/food-list");
        },
        error => {
          alert('Failed to register food menu item. Please try again.');
          console.error('Error:', error);
        }
      );
    } else {
      alert('Please fill in all required fields');
    }
  }
}
