 <mat-toolbar class="header"  >
  <span class="logo">TheFoodExpress</span>
  <span class="spacer"></span>

 <button class="registration-btn" mat-button  id="home" routerLink="/" >Home</button>
  <button class="registration-btn" mat-button  routerLink="/registration">Register</button>

  <div *ngIf="authService.isAuthenticatedUser(); else loginLink">
    <!-- Display user email and logout button if the user is authenticated -->
    <span>{{ authService.getUserEmail }}</span>
    <button (click)="logout()">Logout</button>
  </div>

  <!-- <button class="login-btn" mat-button *ngIf="authService.isAuthenticatedUser(); else loginLink">

    <a (click)="logout()">Logout</a>
  </button> -->

  <ng-template #loginLink>
    <button class="login-btn" mat-button routerLink="/login">Login</button>
  </ng-template>


</mat-toolbar>
