import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-food-card',
  templateUrl: './food-card.component.html',
  styleUrls: ['./food-card.component.css']
})
export class FoodCardComponent {
  @Input() food: any;

}

<mat-card  class="restaurant">
  <img mat-card-image src="../../assets/Food/{{food.image}}.jp" id="img">
  <mat-card-header>
    <mat-card-title>{{food.itemId}}</mat-card-title>
  </mat-card-header>
  <mat-card-content>

    <mat-card-subtitle class="content">{{food.description}}</mat-card-subtitle>
    <mat-card-subtitle class="content">{{food.price}}</mat-card-subtitle>

  </mat-card-content>


</mat-card>

<mat-card class="restaurant">
  <img mat-card-image id="image" src="./../assets/restaurantimage/{{rest.restaurantName}}.jpg" routerLink="/food-list" >
  <mat-card-header class="allcontent">
      <mat-card-title class="restaurant-title">{{rest.restaurantName}}</mat-card-title>
      <mat-card-subtitle class="content">Description: {{rest.description}} <br> Location: {{rest.location}} <br> Rating: {{rest.rating}}</mat-card-subtitle>
  </mat-card-header>

</mat-card>
