import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-food-card',
  templateUrl: './food-card.component.html',
  styleUrls: ['./food-card.component.css']
})
export class FoodCardComponent {
  @Input() food: any;

}

<mat-card  class="restaurant">
  <img mat-card-image src="../../assets/food/{{food.foodImage}}.jpg" id="img">
  <mat-card-header>
    <mat-card-title>{{food.it}}</mat-card-title>
  </mat-card-header>
  <mat-card-content >

    <mat-card-subtitle class="content">{{food?.foodDescription}}</mat-card-subtitle>
    <mat-card-subtitle class="content">{{food?.price}}</mat-card-subtitle>

  </mat-card-content>

</mat-card>
