<mat-toolbar class="header">
  <span class="logo">TheFoodExpress</span>
  <span class="spacer"></span>

  <button class="registration-btn" mat-button id="home" routerLink="/">Home</button>
  <button class="registration-btn" mat-button routerLink="/registration">Register</button>


  <div *ngIf="loggedInUser" class="dropdown mr-5" matDropdown>
    <button mat-button [matMenuTriggerFor]="userMenu" class="dropdown-toggle text-light">
      {{  loggedInUser.split('@')[0] }}
    </button>
    <mat-menu #userMenu="matMenu">
      <button mat-menu-item (click)="onLogout()">
        <i class="fas fa-sign-out-alt"></i> Logout
      </button>
    </mat-menu>

  </div>

</mat-toolbar>
