import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { RestaurantService } from '../service/restaurant.service';}// Replace 'YourService' with the actual service name

@Component({
  selector: 'app-admin-food-item',
  templateUrl: './admin-food-item.component.html',
  styleUrls: ['./admin-food-item.component.css']
})
export class AdminFoodItemComponent {
  addform: FormGroup;

  constructor(private fb: FormBuilder, private RestaurantService: YourService) { // Replace 'YourService' with the actual service name
    this.addform = this.fb.group({
      itemId: ['', Validators.required],
      itemName: ['', Validators.required],
      imageUrl: ['', Validators.required],
      price: ['', Validators.required],
      restaurantId: [''], // Assuming restaurantId is part of your form
    });
  }

  addfoodItem() {
    if (this.addform.valid) {
      const restaurantId = this.addform.get('restaurantId').value; // Extract restaurantId from the form
      this.yourService.addFoodItem(this.addform.value, restaurantId).subscribe(
        (response) => {
          console.log('Food item added successfully', response);
          // Add any additional logic after successful addition
        },
        (error) => {
          console.error('Error adding food item', error);
          // Handle error, show alert, etc.
        }
      );
    }
  }
}
