import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { RestaurantService } from '../service/restaurant.service';
import { TokenInterceptorService } from '../service/token-interceptor.service';

@Component({
  selector: 'app-admin-food-item',
  templateUrl: './admin-food-item.component.html',
  styleUrls: ['./admin-food-item.component.css']
})
export class AdminFoodItemComponent implements OnInit {
  addform: FormGroup;

  constructor(
    private fb: FormBuilder,
    private service: RestaurantService,
    private tokenService: TokenInterceptorService
  ) {}

  ngOnInit() {
    // Initialize the form with required validators
    this.addform = this.fb.group({
      restaurantId: ['', Validators.required],  // Add restaurantId field with required validation
      // Add other fields for food menu
      itemId: ['', Validators.required],
      itemName: ['', Validators.required],
      description: ['', Validators.required],
      price: ['', Validators.required],
    });
  }

  addfoodItem() {
    const role = this.tokenService.getUserEmail();

    if (role === "admin@gmail.com") {
      if (this.addform.valid) {
        const restaurantId = this.addform.value.restaurantId;

        // Call the service method to update food menu for the specified restaurantId
        this.service.updateFoodMenu(restaurantId, this.addform.value).subscribe(
          response => {
            alert(`Food item is added successfully: ${response}`);
            // Reset the form after successful addition
            this.addform.reset();
          },
          error => {
            alert(`Failed to add food item: ${error}`);
          }
        );
      }
    } else {
      alert("You are not authorized to add food items.");
    }
  }
}
<section>
<div id="formBxFoodItem" class="form-box">

  <h2 class="form-title">Add New Food Item:</h2>

  <form [formGroup]="addform">

    <mat-form-field appearance="fill">
    <input matInput type="text" name="restaurantId"placeholder="Enter Restaurant Id" formControlName="restaurantId" required>
    </mat-form-field>
    <br>

      <mat-form-field appearance="fill">
          <input matInput type="text" name="itemId" placeholder="Item Id" formControlName="itemId" required>
      </mat-form-field>
      <br>
      <mat-form-field appearance="fill"><input matInput type="text" name="description"
        placeholder=" item description " formControlName="description" required></mat-form-field>
     <br>
      <mat-form-field appearance="fill">
          <input matInput type="text" name="itemName" placeholder="Item Name" formControlName="itemName"
              required>
      </mat-form-field>

      <br>
      <mat-form-field appearance="fill">
        <input matInput type="text" name="imageUrl" placeholder="ImageUrl of the foodItem"
            formControlName="imageUrl" required>
    </mat-form-field>
    <br>

      <mat-form-field appearance="fill">
          <input matInput type="number" name="price" placeholder="Price" formControlName="price" required>
      </mat-form-field>
      <br>

      <button type="submit" mat-raised-button color="accent" [disabled]="!addform.valid"
          (click)="addfoodItem()">Add Food Item</button>
  </form>
</div>

</section>
